import json
import pandas as pd
from collections import defaultdict

def analyze_spelling_variants(data, spelling_variants):
    results = []

    total_unique_editors = set()
    total_uses = 0

    for variants in spelling_variants:
        base_form = variants[0]  # The base form for this group (first item in the list)
        unique_editors = set()
        total_variant_uses = 0

        for variant in variants:
            if variant in data:
                editors = data[variant]
                unique_editors.update(editors.keys())
                total_variant_uses += sum(editors.values())

        # Update total editors and uses for the entire investigation
        total_unique_editors.update(unique_editors)
        total_uses += total_variant_uses

        # Append the consolidated data for this group of variants
        results.append({
            'Base Form': base_form,
            'Unique Editors': len(unique_editors),
            'Total Instances': total_variant_uses
        })

    # Add total summary row
    results.append({
        'Base Form': 'Total',
        'Unique Editors': len(total_unique_editors),
        'Total Instances': total_uses
    })

    # Convert results to DataFrame
    df = pd.DataFrame(results)
    return df

